import React, { useState, useEffect } from 'react';
import { useQuery, useMutation, useQueryClient } from '@tanstack/react-query';
import { useAuth } from '../contexts/AuthContext';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '../components/ui/Card';
import { Button } from '../components/ui/Button';
import toast from 'react-hot-toast';

const Tasks = () => {
  const { user } = useAuth();

  // Mock tasks data - replace with actual API call
  const { data: tasks, isLoading } = useQuery({
    queryKey: ['tasks'],
    queryFn: () => Promise.resolve({
      data: [
        {
          id: 1,
          title: 'Complete Daily Investment Task',
          description: 'Watch the daily investment video and complete the verification',
          status: 'pending',
          reward: 2.50,
          type: 'daily'
        },
        {
          id: 2,
          title: 'Refer a Friend',
          description: 'Invite a friend to join the platform and earn bonus rewards',
          status: 'available',
          reward: 10.00,
          type: 'referral'
        },
        {
          id: 3,
          title: 'Update Profile Information',
          description: 'Complete your profile with accurate information',
          status: 'completed',
          reward: 5.00,
          type: 'profile'
        }
      ]
    })
  });

  const getStatusColor = (status) => {
    switch (status) {
      case 'completed':
        return 'bg-green-100 text-green-800';
      case 'pending':
        return 'bg-yellow-100 text-yellow-800';
      case 'available':
        return 'bg-blue-100 text-blue-800';
      default:
        return 'bg-gray-100 text-gray-800';
    }
  };

  const getStatusText = (status) => {
    switch (status) {
      case 'completed':
        return 'Completed';
      case 'pending':
        return 'Pending';
      case 'available':
        return 'Available';
      default:
        return 'Unknown';
    }
  };

  const formatCurrency = (amount) => {
    return new Intl.NumberFormat('en-US', {
      style: 'currency',
      currency: 'USD',
      minimumFractionDigits: 2,
      maximumFractionDigits: 2
    }).format(amount);
  };

  return (
