import React, { useState, useEffect } from 'react';
import { useQuery } from '@tanstack/react-query';
import { Card, CardContent, CardHeader, CardTitle } from './ui/Card';
import { Button } from './ui/Button';
import { depositAPI } from '../services/api';
import toast from 'react-hot-toast';

const UsdtDeposit = ({ onClose, vipLevel = null }) => {
  const [selectedNetwork, setSelectedNetwork] = useState('BEP20');

  // Fetch USDT addresses from API
  const { data: addressesData, isLoading: addressesLoading } = useQuery({
    queryKey: ['usdtAddresses'],
    queryFn: () => depositAPI.getUsdtAddresses(),
  });

  const walletAddress = addressesData?.data?.[selectedNetwork] || '';
  const qrCodeUrl = walletAddress ? `https://api.qrserver.com/v1/create-qr-code/?size=200x200&data=${walletAddress}` : '';

  const handleCopyAddress = async () => {
    try {
      await navigator.clipboard.writeText(walletAddress);
      toast.success('Wallet address copied to clipboard!');
    } catch (err) {
      toast.error('Failed to copy address');
    }
  };

  const handleNetworkChange = (network) => {
    setSelectedNetwork(network);
  };

  const formatAddress = (address) => {
    if (address.length > 20) {
      return `${address.substring(0, 20)}...`;
    }
    return address;
  };

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center p-4 z-50">
      <div className="bg-white rounded-lg w-full max-w-md max-h-[90vh] overflow-y-auto">
        {/* Header */}
        <div className="flex items-center justify-between p-4 border-b">
          <button
            onClick={onClose}
            className="p-2 hover:bg-gray-100 rounded-full"
          >
            <svg className="w-6 h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M15 19l-7-7 7-7" />
            </svg>
          </button>
          <h2 className="text-lg font-semibold">USDT</h2>
          <div className="w-10"></div> {/* Spacer for centering */}
        </div>

        <div className="p-4">
          {/* Network Selection */}
          <div className="flex bg-gray-100 rounded-lg p-1 mb-6">
            <button
              onClick={() => handleNetworkChange('BEP20')}
              className={`flex-1 py-2 px-4 rounded-md text-sm font-medium transition-colors ${
                selectedNetwork === 'BEP20'
                  ? 'bg-red-500 text-white shadow-sm'
                  : 'text-gray-600 hover:text-gray-800'
              }`}
            >
              BEP20
            </button>
            <button
              onClick={() => handleNetworkChange('TRC20')}
              className={`flex-1 py-2 px-4 rounded-md text-sm font-medium transition-colors ${
                selectedNetwork === 'TRC20'
                  ? 'bg-red-500 text-white shadow-sm'
                  : 'text-gray-600 hover:text-gray-800'
              }`}
            >
              TRC20
            </button>
          </div>

          {/* VIP Level Info */}
          {vipLevel && (
            <div className="bg-blue-50 border border-blue-200 rounded-lg p-4 mb-6">
              <div className="text-center">
                <h3 className="font-semibold text-blue-800">Join {vipLevel.name}</h3>
                <p className="text-blue-600 text-sm">
                  Deposit ${vipLevel.amount} to join this VIP level
                </p>
              </div>
            </div>
          )}

          {/* QR Code Section */}
          <div className="text-center mb-6">
            <h3 className="text-lg font-semibold mb-4">Recharge QR code</h3>
            {addressesLoading ? (
              <div className="w-48 h-48 bg-gray-200 rounded-lg mx-auto animate-pulse"></div>
            ) : (
              <div className="w-48 h-48 mx-auto">
                <img
                  src={qrCodeUrl}
                  alt="USDT Deposit QR Code"
                  className="w-full h-full rounded-lg border"
                />
              </div>
            )}
          </div>

          {/* Wallet Address */}
          <div className="mb-6">
            <div className="flex items-center justify-between bg-gray-50 rounded-lg p-3">
              <span className="text-red-500 font-mono text-sm">
                {formatAddress(walletAddress)}
              </span>
              <Button
                onClick={handleCopyAddress}
                className="bg-red-500 hover:bg-red-600 text-white px-4 py-2 rounded-lg text-sm"
              >
                Copy
              </Button>
            </div>
          </div>

          {/* Instructions */}
          <div className="space-y-4 text-sm text-gray-600">
            <div className="bg-gray-50 rounded-lg p-4">
              <ol className="space-y-3">
                <li className="flex items-start">
                  <span className="font-semibold mr-2">1.</span>
                  <span>
                    Please bind the wallet address that supports USDT-BEP20 or USDT-TRC20 network withdrawal. Other currencies have yet to gain support.
                  </span>
                </li>
                <li className="flex items-start">
                  <span className="font-semibold mr-2">2.</span>
                  <span>
                    Withdraw once a day, no time limit, zero withdrawal fees, funds can be deposited within 1-3 minutes after withdrawal.
                  </span>
                </li>
                <li className="flex items-start">
                  <span className="font-semibold mr-2">3.</span>
                  <span>
                    If the funds have not been accounted for a long time, please refresh the page or contact customer service.
                  </span>
                </li>
                <li className="flex items-start">
                  <span className="font-semibold mr-2">4.</span>
                  <span>
                    TRC-20 network minimum withdrawal 5USDT, BEP-20 network minimum withdrawal 1USDT.
                  </span>
                </li>
              </ol>
            </div>
          </div>

          {/* Action Buttons */}
          <div className="mt-6 space-y-3">
            <Button
              onClick={onClose}
              className="w-full bg-gray-500 hover:bg-gray-600 text-white"
            >
              Close
            </Button>
            {vipLevel && (
              <Button
                onClick={() => {
                  toast.success('Please complete your USDT deposit to join the VIP level');
                  onClose();
                }}
                className="w-full bg-blue-500 hover:bg-blue-600 text-white"
              >
                I've Made the Deposit
              </Button>
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

export default UsdtDeposit;
