import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { useQuery } from '@tanstack/react-query';
import { useAuth } from '../contexts/AuthContext';
import { walletAPI, referralAPI, vipAPI } from '../services/api';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '../components/ui/Card';
import { Button } from '../components/ui/Button';
import VipDashboard from '../components/VipDashboard';
import {
  ResponsiveContainer,
  LineChart,
  Line,
  XAxis,
  YAxis,
  CartesianGrid,
  Tooltip
} from 'recharts';

const Dashboard = () => {
  const { user } = useAuth();
  const [activeTab, setActiveTab] = useState('home');
  const [timeRange, setTimeRange] = useState('30d');

  // Fetch wallet stats
  const { data: walletStats, isLoading: walletLoading } = useQuery({
    queryKey: ['walletStats'],
    queryFn: () => walletAPI.getStats(),
  });

  // Fetch referral stats
  const { data: referralStats, isLoading: referralLoading } = useQuery({
    queryKey: ['referralStats'],
    queryFn: () => referralAPI.getStats(),
  });

  // Fetch recent transactions
  const { data: transactions, isLoading: transactionsLoading } = useQuery({
    queryKey: ['transactions', { limit: 5 }],
    queryFn: () => walletAPI.getTransactions({ limit: 5, page: 1 }),
  });

  // Fetch projected earnings data for chart
  const { data: projectedEarnings } = useQuery({
    queryKey: ['projectedEarnings', timeRange],
    queryFn: () => walletAPI.getProjectedEarnings({ timeRange }),
    enabled: !!timeRange
  });

  // Fetch VIP levels
  const { data: vipLevels, isLoading: vipLoading } = useQuery({
    queryKey: ['vipLevels'],
    queryFn: () => vipAPI.getLevels(),
  });

  // Fetch user VIP status
  const { data: userVipStatus } = useQuery({
    queryKey: ['userVipStatus'],
    queryFn: () => vipAPI.getStatus(),
  });

  const formatCurrency = (amount) => {
    return new Intl.NumberFormat('en-US', {
      style: 'currency',
      currency: 'USD',
      minimumFractionDigits: 2,
      maximumFractionDigits: 8
    }).format(amount);
  };

  const formatDate = (dateString) => {
    return new Date(dateString).toLocaleDateString('en-US', {
      month: 'short',
      day: 'numeric',
      hour: '2-digit',
      minute: '2-digit'
    });
  };

  const getVipLevelColor = (index) => {
    const colors = [
      'bg-gray-500',      // Starter
      'bg-amber-600',     // Bronze
      'bg-gray-400',      // Silver
      'bg-yellow-500',    // Gold
      'bg-gray-700',      // Platinum
      'bg-blue-500',      // Diamond
      'bg-purple-600',    // Elite
      'bg-red-600',       // Master
      'bg-indigo-700',    // Legend
      'bg-black'          // Supreme
    ];
    return colors[index] || 'bg-emerald-500';
  };

  const handleJoinVip = async (vipLevelId) => {
    try {
      const response = await vipAPI.joinVip(vipLevelId);
      if (response.data.success) {
        // Refresh data after successful VIP join
        window.location.reload();
      } else {
        alert(response.data.message || 'Failed to join VIP level');
      }
    } catch (error) {
      console.error('Error joining VIP:', error);
      const errorMessage = error.response?.data?.message || 'Failed to join VIP level. Please try again.';
      alert(errorMessage);
    }
  };

  const getTransactionIcon = (type) => {
    switch (type) {
      case 'DEPOSIT':
        return (
          <div className="w-8 h-8 bg-green-100 rounded-full flex items-center justify-center">
            <svg className="w-4 h-4 text-green-600" fill="none" stroke="currentColor" viewBox="0 0 24 24">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 6v6m0 0v6m0-6h6m-6 0H6" />
            </svg>
          </div>
        );
      case 'WITHDRAWAL':
        return (
          <div className="w-8 h-8 bg-red-100 rounded-full flex items-center justify-center">
            <svg className="w-4 h-4 text-red-600" fill="none" stroke="currentColor" viewBox="0 0 24 24">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M20 12H4" />
            </svg>
          </div>
        );
      case 'GROWTH':
        return (
          <div className="w-8 h-8 bg-blue-100 rounded-full flex items-center justify-center">
            <svg className="w-4 h-4 text-blue-600" fill="none" stroke="currentColor" viewBox="0 0 24 24">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M13 7h8m0 0v8m0-8l-8 8-4-4-6 6" />
            </svg>
          </div>
        );
      case 'REFERRAL_BONUS':
        return (
          <div className="w-8 h-8 bg-purple-100 rounded-full flex items-center justify-center">
            <svg className="w-4 h-4 text-purple-600" fill="none" stroke="currentColor" viewBox="0 0 24 24">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M17 20h5v-2a3 3 0 00-5.356-1.857M17 20H7m10 0v-2c0-.656-.126-1.283-.356-1.857M7 20H2v-2a3 3 0 015.356-1.857M7 20v-2c0-.656.126-1.283.356-1.857m0 0a5.002 5.002 0 019.288 0M15 7a3 3 0 11-6 0 3 3 0 016 0zm6 3a2 2 0 11-4 0 2 2 0 014 0zM7 10a2 2 0 11-4 0 2 2 0 014 0z" />
            </svg>
          </div>
        );
      default:
        return (
          <div className="w-8 h-8 bg-gray-100 rounded-full flex items-center justify-center">
            <svg className="w-4 h-4 text-gray-600" fill="none" stroke="currentColor" viewBox="0 0 24 24">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 8c-1.657 0-3 .895-3 2s1.343 2 3 2 3 .895 3 2-1.343 2-3 2m0-8c1.11 0 2.08.402 2.599 1M12 8V7m0 1v8m0 0v1m0-1c-1.11 0-2.08-.402-2.599-1" />
            </svg>
          </div>
        );
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-emerald-400 via-teal-500 to-cyan-600">
      {/* Header */}
      <div className="bg-gradient-to-r from-emerald-500 to-teal-600 px-4 py-6">
        <div className="flex items-center justify-between">
          <div className="flex items-center space-x-3">
            <div className="w-8 h-8 bg-white rounded-full flex items-center justify-center">
              <svg className="w-5 h-5 text-emerald-600" fill="currentColor" viewBox="0 0 20 20">
                <path d="M3 4a1 1 0 011-1h12a1 1 0 011 1v2a1 1 0 01-1 1H4a1 1 0 01-1-1V4zM3 10a1 1 0 011-1h6a1 1 0 011 1v6a1 1 0 01-1 1H4a1 1 0 01-1-1v-6zM14 9a1 1 0 00-1 1v6a1 1 0 001 1h2a1 1 0 001-1v-6a1 1 0 00-1-1h-2z" />
              </svg>
            </div>
            <div>
              <h1 className="text-white text-lg font-semibold">Language selection</h1>
            </div>
          </div>
          <div className="flex items-center space-x-3">
            <div className="w-8 h-8 bg-white/20 rounded-full flex items-center justify-center">
              <svg className="w-5 h-5 text-white" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M15 17h5l-5 5-5-5h5v-13z" />
              </svg>
            </div>
            <div className="w-8 h-8 bg-red-500 rounded-full flex items-center justify-center">
              <svg className="w-5 h-5 text-white" fill="currentColor" viewBox="0 0 20 20">
                <path fillRule="evenodd" d="M18 10a8 8 0 11-16 0 8 8 0 0116 0zm-7-4a1 1 0 11-2 0 1 1 0 012 0zM9 9a1 1 0 000 2v3a1 1 0 001 1h1a1 1 0 100-2v-3a1 1 0 00-1-1H9z" clipRule="evenodd" />
              </svg>
            </div>
          </div>
        </div>
      </div>

      {/* Content */}
      <div className="px-4 py-6 space-y-6">
        {/* Certificates Section */}
        <div className="bg-white rounded-lg p-4">
          <div className="flex items-center justify-between mb-4">
            <h2 className="text-gray-800 font-medium">📋 operate 24 hours a day, 7 days a week. We believe that the future of travel will no</h2>
          </div>
          <div className="grid grid-cols-5 gap-2">
            {['CGC', 'FCC', 'MSDS', 'CE', 'Shipping Certificate'].map((cert, index) => (
              <div key={index} className="bg-gray-100 rounded-lg p-2 text-center">
                <div className="w-full h-16 bg-white rounded border mb-2 flex items-center justify-center">
                  <div className="text-xs text-gray-500">{cert}</div>
                </div>
                <div className="text-xs text-gray-600">{cert}</div>
              </div>
            ))}
          </div>
        </div>

        {/* Video Section */}
        <div className="bg-black rounded-lg overflow-hidden relative">
          <div className="aspect-video flex items-center justify-center">
            <div className="w-16 h-16 bg-white/20 rounded-full flex items-center justify-center">
              <svg className="w-8 h-8 text-white ml-1" fill="currentColor" viewBox="0 0 20 20">
                <path d="M6.3 2.841A1.5 1.5 0 004 4.11V15.89a1.5 1.5 0 002.3 1.269l9.344-5.89a1.5 1.5 0 000-2.538L6.3 2.84z" />
              </svg>
            </div>
          </div>
          <div className="absolute bottom-4 left-4 text-white font-mono text-lg">01:25</div>
        </div>

        {/* News Section */}
        <div className="bg-white rounded-lg overflow-hidden">
          <div className="bg-gradient-to-r from-emerald-500 to-teal-600 px-4 py-3">
            <h2 className="text-white text-lg font-semibold flex items-center">
              📰 NEWS
            </h2>
          </div>
          <div className="p-4">
            <div className="flex items-center space-x-3">
              <div className="w-16 h-16 bg-emerald-100 rounded-lg flex items-center justify-center">
                <div className="w-8 h-8 bg-emerald-500 rounded"></div>
              </div>
              <div className="flex-1">
                <p className="text-gray-800 text-sm">Latest updates and announcements</p>
              </div>
            </div>
          </div>
        </div>

        {/* VIP Levels Section */}
        <div className="bg-white rounded-lg overflow-hidden">
          <div className="bg-gradient-to-r from-emerald-500 to-teal-600 px-4 py-3">
            <h2 className="text-white text-lg font-semibold flex items-center">
              💎 VIP LEVELS
            </h2>
          </div>
          <div className="p-4">
            {vipLoading ? (
              <div className="text-center py-8">
                <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-emerald-500 mx-auto"></div>
                <p className="text-gray-500 mt-2">Loading VIP levels...</p>
              </div>
            ) : (
              <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
                {vipLevels?.data?.map((level, index) => {
                  const isCurrentLevel = userVipStatus?.data?.vipLevel?.id === level.id;
                  const canAfford = walletStats?.data?.balance >= level.amount;

                  return (
                    <div 
                      key={level.id} 
                      className="relative rounded-lg p-4 border-2 transition-all duration-300 hover:shadow-lg border-gray-200 bg-white hover:border-emerald-300"
                    >
                      {isCurrentLevel && (
                        <div className="absolute -top-2 -right-2 bg-emerald-500 text-white text-xs px-2 py-1 rounded-full font-semibold">
                          ACTIVE
                        </div>
                      )}

                      <div className="flex items-center justify-between mb-3">
                        <div className={`w-10 h-10 rounded-full flex items-center justify-center text-white font-bold text-sm ${
                          getVipLevelColor(index)
                        }`}>
                          {level.name.charAt(0)}
                        </div>
                        <div className="text-right">
                          <div className="text-xs text-gray-500">Investment</div>
                          <div className="font-semibold text-gray-800">${level.amount}</div>
                        </div>
                      </div>

                      <h3 className="font-semibold text-lg mb-2 text-gray-800">{level.name}</h3>

                      <div className="space-y-2 mb-4">
                        <div className="flex justify-between items-center">
                          <span className="text-sm text-gray-600">Daily Earning:</span>
                          <span className="font-semibold text-emerald-600">${level.dailyEarning}</span>
                        </div>
                        <div className="flex justify-between items-center">
                          <span className="text-sm text-gray-600">Monthly ROI:</span>
                          <span className="font-semibold text-blue-600">
                            {((level.dailyEarning * 30 / level.amount) * 100).toFixed(1)}%
                          </span>
                        </div>
                        <div className="flex justify-between items-center">
                          <span className="text-sm text-gray-600">Payback Period:</span>
                          <span className="font-semibold text-purple-600">
                            {Math.ceil(level.amount / level.dailyEarning)} days
                          </span>
                        </div>
                      </div>

                      <Button 
                        className={`w-full ${
                          isCurrentLevel
                            ? 'bg-emerald-500 hover:bg-emerald-600 text-white'
                            : canAfford 
                              ? 'bg-blue-500 hover:bg-blue-600 text-white' 
                              : 'bg-orange-500 hover:bg-orange-600 text-white'
                        }`}
                        onClick={() => handleJoinVip(level.id)}
                      >
                        {isCurrentLevel 
                          ? 'Renew Level' 
                          : canAfford 
                            ? `Join for $${level.amount}` 
                            : `Join for $${level.amount}`
                        }
                      </Button>

                      {!canAfford && (
                        <p className="text-xs text-orange-600 mt-2 text-center">
                          Insufficient balance - Need ${(level.amount - (walletStats?.data?.balance || 0)).toFixed(2)} more
                        </p>
                      )}
                    </div>
                  );
                })}
              </div>
            )}
          </div>
        </div>

        {/* Current VIP Status */}
        {userVipStatus?.data?.vipLevel && (
          <div className="bg-gradient-to-br from-emerald-400 to-teal-500 rounded-lg p-4 text-white">
            <div className="flex items-center justify-between">
              <div>
                <h3 className="font-semibold text-lg">Your Current VIP</h3>
                <p className="text-emerald-100">{userVipStatus.data.vipLevel.name}</p>
              </div>
              <div className="text-right">
                <div className="text-emerald-100 text-sm">Daily Earnings</div>
                <div className="font-bold text-xl">${userVipStatus.data.vipLevel.dailyEarning}</div>
              </div>
            </div>
            <div className="mt-4 pt-4 border-t border-emerald-300">
              <div className="flex justify-between text-sm">
                <span>Total Paid:</span>
                <span>${userVipStatus.data.totalPaid}</span>
              </div>
              <div className="flex justify-between text-sm mt-1">
                <span>Member Since:</span>
                <span>{formatDate(userVipStatus.data.createdAt)}</span>
              </div>
            </div>
          </div>
        )}

        {/* Legacy VIP Cards */}
        <div className="grid grid-cols-2 gap-4">
          <div className="bg-gradient-to-br from-emerald-400 to-teal-500 rounded-lg p-4 text-white">
            <div className="w-8 h-8 bg-white/20 rounded mb-3">
              <div className="w-full h-full bg-emerald-600 rounded flex items-center justify-center">
                <div className="text-xs font-bold">VIP</div>
              </div>
            </div>
            <h3 className="font-semibold mb-1">VIP0</h3>
            <p className="text-sm opacity-90 mb-2">Delivery in progress</p>
            <div className="text-xs opacity-75">
              <div>Expected daily income</div>
              <div className="font-semibold">{formatCurrency(walletStats?.data?.balance || 0)}</div>
            </div>
          </div>

          <div className="bg-gradient-to-br from-emerald-400 to-teal-500 rounded-lg p-4 text-white">
            <div className="w-8 h-8 bg-white/20 rounded mb-3">
              <div className="w-full h-full bg-emerald-600 rounded flex items-center justify-center">
                <div className="text-xs font-bold">VIP</div>
              </div>
            </div>
            <h3 className="font-semibold mb-1">VIP1</h3>
            <p className="text-sm opacity-90 mb-2">Not delivered yet</p>
            <div className="text-xs opacity-75">
              <div>Expected daily income</div>
              <div className="font-semibold">{formatCurrency(2.00)}</div>
            </div>
          </div>
        </div>

        {/* Management Log */}
        <div className="bg-white rounded-lg p-4">
          <h3 className="text-gray-800 font-medium mb-3">Management log</h3>
          <div className="bg-gray-50 rounded-lg p-3">
            <div className="flex items-center space-x-3">
              <div className="w-8 h-8 bg-gray-200 rounded flex items-center justify-center">
                <div className="w-4 h-4 bg-gray-400 rounded"></div>
              </div>
              <div className="flex-1">
                <div className="text-sm text-gray-600">***5918</div>
                <div className="text-xs text-gray-500">Returned</div>
              </div>
              <div className="text-right">
                <div className="text-sm text-gray-600">Time of use</div>
                <div className="text-xs text-gray-500">2.81 Hour</div>
                <div className="text-xs text-gray-500 mt-1">Current power</div>
                <div className="text-xs text-gray-500">58%</div>
              </div>
            </div>
          </div>
        </div>
      </div>

        <div className="grid grid-cols-1 lg:grid-cols-3 gap-6">
          {/* Portfolio Growth Chart */}
          <Card className="lg:col-span-2">
            <CardHeader>
              <div className="flex items-center justify-between">
                <CardTitle>Portfolio Growth</CardTitle>
                <div className="flex space-x-2">
                  {['7d', '30d', '90d'].map((range) => (
                    <button
                      key={range}
                      onClick={() => setTimeRange(range)}
                      className={`px-3 py-1 text-sm rounded-md ${
                        timeRange === range
                          ? 'bg-blue-100 text-blue-700'
                          : 'text-gray-500 hover:text-gray-700'
                      }`}
                    >
                      {range}
                    </button>
                  ))}
                </div>
              </div>
            </CardHeader>
            <CardContent>
              <div className="h-80">
                {projectedEarnings?.data?.chartData ? (
                  <ResponsiveContainer width="100%" height="100%">
                    <LineChart data={projectedEarnings.data.chartData}>
                      <CartesianGrid strokeDasharray="3 3" />
                      <XAxis 
                        dataKey="date" 
                        tickFormatter={(value) => new Date(value).toLocaleDateString('en-US', { month: 'short', day: 'numeric' })}
                      />
                      <YAxis tickFormatter={(value) => `$${value.toFixed(2)}`} />
                      <Tooltip 
                        labelFormatter={(value) => new Date(value).toLocaleDateString()}
                        formatter={(value) => [formatCurrency(value), 'Balance']}
                      />
                      <Line 
                        type="monotone" 
                        dataKey="balance" 
                        stroke="#3B82F6" 
                        strokeWidth={2}
                        dot={false}
                      />
                    </LineChart>
                  </ResponsiveContainer>
                ) : (
                  <div className="flex items-center justify-center h-full text-gray-500">
                    Loading chart data...
                  </div>
                )}
              </div>
            </CardContent>
          </Card>

          {/* Quick Actions */}
          <Card>
            <CardHeader>
              <CardTitle>Quick Actions</CardTitle>
              <CardDescription>
                Manage your crypto portfolio
              </CardDescription>
            </CardHeader>
            <CardContent className="space-y-4">
              <Button asChild className="w-full">
                <Link to="/deposit">
                  <svg className="w-4 h-4 mr-2" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 6v6m0 0v6m0-6h6m-6 0H6" />
                  </svg>
                  Make Deposit
                </Link>
              </Button>

              <Button asChild variant="outline" className="w-full">
                <Link to="/withdraw">
                  <svg className="w-4 h-4 mr-2" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M20 12H4" />
                  </svg>
                  Request Withdrawal
                </Link>
              </Button>

              <Button asChild variant="outline" className="w-full">
                <Link to="/referral">
                  <svg className="w-4 h-4 mr-2" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M8.684 13.342C8.886 12.938 9 12.482 9 12c0-.482-.114-.938-.316-1.342m0 2.684a3 3 0 110-2.684m0 2.684l6.632 3.316m-6.632-6l6.632-3.316m0 0a3 3 0 105.367-2.684 3 3 0 00-5.367 2.684zm0 9.316a3 3 0 105.367 2.684 3 3 0 00-5.367-2.684z" />
                  </svg>
                  Share Referral
                </Link>
              </Button>

              <Button asChild variant="ghost" className="w-full">
                <Link to="/profile">
                  <svg className="w-4 h-4 mr-2" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M16 7a4 4 0 11-8 0 4 4 0 018 0zM12 14a7 7 0 00-7 7h14a7 7 0 00-7-7z" />
                  </svg>
                  View Profile
                </Link>
              </Button>
            </CardContent>
          </Card>
        </div>

        {/* VIP Dashboard Section */}
        <div className="mb-8">
          <VipDashboard />
        </div>

        {/* Recent Transactions */}
        <Card className="mt-6">
          <CardHeader>
            <div className="flex items-center justify-between">
              <CardTitle>Recent Transactions</CardTitle>
              <Button asChild variant="outline" size="sm">
                <Link to="/transactions">View All</Link>
              </Button>
            </div>
          </CardHeader>
          <CardContent>
            {transactionsLoading ? (
              <div className="space-y-4">
                {[...Array(3)].map((_, i) => (
                  <div key={i} className="flex items-center space-x-4 animate-pulse">
                    <div className="w-8 h-8 bg-gray-200 rounded-full"></div>
                    <div className="flex-1">
                      <div className="h-4 bg-gray-200 rounded w-1/4 mb-2"></div>
                      <div className="h-3 bg-gray-200 rounded w-1/2"></div>
                    </div>
                    <div className="h-4 bg-gray-200 rounded w-20"></div>
                  </div>
                ))}
              </div>
            ) : transactions?.data?.transactions?.length > 0 ? (
              <div className="space-y-4">
                {transactions.data.transactions.map((transaction) => (
                  <div key={transaction.id} className="flex items-center space-x-4">
                    {getTransactionIcon(transaction.type)}
                    <div className="flex-1 min-w-0">
                      <p className="text-sm font-medium text-gray-900 truncate">
                        {transaction.description}
                      </p>
                      <p className="text-sm text-gray-500">
                        {formatDate(transaction.createdAt)}
                      </p>
                    </div>
                    <div className="text-sm font-medium">
                      <span className={transaction.amount >= 0 ? 'text-green-600' : 'text-red-600'}>
                        {transaction.amount >= 0 ? '+' : ''}{formatCurrency(transaction.amount)}
                      </span>
                    </div>
                  </div>
                ))}
              </div>
            ) : (
              <div className="text-center py-8">
                <svg className="w-12 h-12 text-gray-400 mx-auto mb-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 5H7a2 2 0 00-2 2v10a2 2 0 002 2h8a2 2 0 002-2V7a2 2 0 00-2-2h-2M9 5a2 2 0 002 2h2a2 2 0 002-2M9 5a2 2 0 012-2h2a2 2 0 012 2" />
                </svg>
                <p className="text-gray-500">No transactions yet</p>
                <p className="text-sm text-gray-400 mt-1">
                  Make your first deposit to get started
                </p>
              </div>
            )}
          </CardContent>
        </Card>
      </div>
  );
};

export default Dashboard;
