import React, { useState } from 'react';
import { useQuery } from '@tanstack/react-query';
import { depositAPI } from '../services/api';
import { Button } from './ui/Button';
import { useAuth } from '../contexts/AuthContext';

const DepositHistory = ({ onClose }) => {
  const { user } = useAuth();
  const [page, setPage] = useState(1);
  const [statusFilter, setStatusFilter] = useState('');

  console.log('User authenticated:', !!user);
  console.log('User object:', user);
  console.log('Token from localStorage:', localStorage.getItem('token'));
  console.log('Auth state:', { user, isAuthenticated: !!user, token: localStorage.getItem('token') });

  // Fetch user's deposits
  const { data: depositsData, isLoading, error } = useQuery({
    queryKey: ['myDeposits'],
    queryFn: async () => {
      console.log('Fetching deposits with params:', { page, limit: 10, statusFilter });
      const response = await depositAPI.getMyDeposits({ 
        page, 
        limit: 10,
        ...(statusFilter && { status: statusFilter })
      });
      console.log('Deposits API response:', response);
      return response;
    },
    enabled: true, // Temporarily enable for debugging
    retry: 1,
    refetchOnWindowFocus: false,
    onError: (error) => {
      console.error('Error fetching deposits:', error);
      console.error('Error response:', error.response);
      console.error('Error status:', error.response?.status);
      console.error('Error data:', error.response?.data);
    },
    onSuccess: (data) => {
      console.log('Deposits query success:', data);
    }
  });

  // Fetch pending count
  const { data: pendingCountData, error: pendingError } = useQuery({
    queryKey: ['pendingDepositsCount'],
    queryFn: () => depositAPI.getPendingCount(),
    enabled: true, // Temporarily enable for debugging
    onError: (error) => {
      console.error('Error fetching pending count:', error);
      console.error('Pending error response:', error.response);
      console.error('Pending error status:', error.response?.status);
      console.error('Pending error data:', error.response?.data);
    },
  });

  // Handle API response structure - deposits and pending count are separate
  const deposits = depositsData?.success && Array.isArray(depositsData?.data) 
    ? depositsData.data 
    : [];
  const pagination = depositsData?.pagination || {};
  const pendingCount = pendingCountData?.data?.pendingCount || 0;

  // Debug logging to see what's happening
  console.log('Deposits data:', depositsData);
  console.log('Pending count data:', pendingCountData);
  console.log('Deposits array:', deposits);
  console.log('Deposits length:', deposits.length);
  console.log('Pending count:', pendingCount);
  console.log('Is loading:', isLoading);
  console.log('Has error:', !!error);

  const getStatusColor = (status) => {
    switch (status) {
      case 'PENDING':
        return 'bg-yellow-500 text-yellow-900';
      case 'CONFIRMED':
        return 'bg-green-500 text-white';
      case 'FAILED':
        return 'bg-red-500 text-white';
      case 'EXPIRED':
        return 'bg-gray-500 text-white';
      default:
        return 'bg-gray-500 text-white';
    }
  };

  const getStatusIcon = (status) => {
    switch (status) {
      case 'PENDING':
        return (
          <svg className="w-4 h-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 8v4l3 3m6-3a9 9 0 11-18 0 9 9 0 0118 0z" />
          </svg>
        );
      case 'CONFIRMED':
        return (
          <svg className="w-4 h-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M5 13l4 4L19 7" />
          </svg>
        );
      case 'FAILED':
        return (
          <svg className="w-4 h-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M6 18L18 6M6 6l12 12" />
          </svg>
        );
      default:
        return (
          <svg className="w-4 h-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M13 16h-1v-4h-1m1-4h.01M21 12a9 9 0 11-18 0 9 9 0 0118 0z" />
          </svg>
        );
    }
  };

  const formatDate = (dateString) => {
    return new Date(dateString).toLocaleDateString('en-US', {
      year: 'numeric',
      month: 'short',
      day: 'numeric',
      hour: '2-digit',
      minute: '2-digit'
    });
  };

  const formatAmount = (amount) => {
    return parseFloat(amount).toFixed(2);
  };

  if (!user) {
    return (
      <div className="fixed inset-0 bg-black/80 backdrop-blur-sm flex items-center justify-center p-4 z-50">
        <div className="backdrop-blur-xl bg-white/10 rounded-2xl w-full max-w-4xl max-h-[90vh] overflow-y-auto border border-white/20 shadow-2xl">
          <div className="p-6">
            <div className="text-center text-red-400">
              <p>Authentication required</p>
              <p className="text-sm text-gray-400 mt-2">
                Please log in to view your deposit history
              </p>
              <Button onClick={onClose} className="mt-4">
                Close
              </Button>
            </div>
          </div>
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="fixed inset-0 bg-black/80 backdrop-blur-sm flex items-center justify-center p-4 z-50">
        <div className="backdrop-blur-xl bg-white/10 rounded-2xl w-full max-w-4xl max-h-[90vh] overflow-y-auto border border-white/20 shadow-2xl">
          <div className="p-6">
            <div className="text-center text-red-400">
              <p>Error loading deposit history</p>
              <p className="text-sm text-gray-400 mt-2">
                {error.response?.data?.message || error.message || 'Please try again later'}
              </p>
              <Button onClick={onClose} className="mt-4">
                Close
              </Button>
            </div>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="fixed inset-0 bg-black/80 backdrop-blur-sm flex items-center justify-center p-4 z-50">
      <div className="backdrop-blur-xl bg-white/10 rounded-2xl w-full max-w-4xl max-h-[90vh] overflow-y-auto border border-white/20 shadow-2xl">
        {/* Header */}
        <div className="flex items-center justify-between p-6 border-b border-white/20">
          <div>
            <h2 className="text-xl font-semibold text-white">Deposit History</h2>
            {pendingCount > 0 && (
              <p className="text-sm text-yellow-400 mt-1">
                {pendingCount} pending deposit{pendingCount !== 1 ? 's' : ''}
              </p>
            )}
          </div>
          <button
            onClick={onClose}
            className="p-2 hover:bg-white/20 rounded-full text-white transition-colors"
          >
            <svg className="w-6 h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M6 18L18 6M6 6l12 12" />
            </svg>
          </button>
        </div>

        <div className="p-6">
          {/* Debug Info - Remove this later */}
          <div className="mb-4 p-4 bg-blue-500/20 rounded-lg border border-blue-400/30">
            <p className="text-blue-300 text-sm">
              Debug: Found {deposits.length} deposits, API success: {depositsData?.success ? 'true' : 'false'}
            </p>
            <p className="text-blue-300 text-sm">
              Pending count: {pendingCount}, Pending API success: {pendingCountData?.success ? 'true' : 'false'}
            </p>
            <p className="text-blue-300 text-sm">
              Deposits data type: {typeof depositsData?.data}, Is array: {Array.isArray(depositsData?.data)}
            </p>
            <p className="text-blue-300 text-sm">
              Deposits error: {error ? 'Yes' : 'No'}, Pending error: {pendingError ? 'Yes' : 'No'}
            </p>
            <p className="text-blue-300 text-sm">
              Error status: {error?.response?.status}, Pending error status: {pendingError?.response?.status}
            </p>
          </div>

          {/* Status Filter */}
          <div className="mb-6">
            <div className="flex flex-wrap gap-2">
              <button
                onClick={() => setStatusFilter('')}
                className={`px-4 py-2 rounded-lg text-sm font-medium transition-colors ${
                  statusFilter === '' 
                    ? 'bg-blue-500 text-white' 
                    : 'bg-white/10 text-gray-300 hover:bg-white/20'
                }`}
              >
                All
              </button>
              <button
                onClick={() => setStatusFilter('PENDING')}
                className={`px-4 py-2 rounded-lg text-sm font-medium transition-colors ${
                  statusFilter === 'PENDING' 
                    ? 'bg-yellow-500 text-yellow-900' 
                    : 'bg-white/10 text-gray-300 hover:bg-white/20'
                }`}
              >
                Pending
              </button>
              <button
                onClick={() => setStatusFilter('CONFIRMED')}
                className={`px-4 py-2 rounded-lg text-sm font-medium transition-colors ${
                  statusFilter === 'CONFIRMED' 
                    ? 'bg-green-500 text-white' 
                    : 'bg-white/10 text-gray-300 hover:bg-white/20'
                }`}
              >
                Confirmed
              </button>
              <button
                onClick={() => setStatusFilter('FAILED')}
                className={`px-4 py-2 rounded-lg text-sm font-medium transition-colors ${
                  statusFilter === 'FAILED' 
                    ? 'bg-red-500 text-white' 
                    : 'bg-white/10 text-gray-300 hover:bg-white/20'
                }`}
              >
                Failed
              </button>
            </div>
          </div>

          {/* Deposits List */}
          {isLoading ? (
            <div className="space-y-4">
              {[...Array(5)].map((_, i) => (
                <div key={i} className="animate-pulse">
                  <div className="bg-white/10 rounded-lg p-4">
                    <div className="h-4 bg-white/20 rounded w-1/4 mb-2"></div>
                    <div className="h-3 bg-white/20 rounded w-1/2"></div>
                  </div>
                </div>
              ))}
            </div>
          ) : !deposits || deposits.length === 0 ? (
            <div className="text-center py-12">
              <div className="w-16 h-16 bg-white/10 rounded-full flex items-center justify-center mx-auto mb-4">
                <svg className="w-8 h-8 text-gray-400" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 12h6m-6 4h6m2 5H7a2 2 0 01-2-2V5a2 2 0 012-2h5.586a1 1 0 01.707.293l5.414 5.414a1 1 0 01.293.707V19a2 2 0 01-2 2z" />
                </svg>
              </div>
              <p className="text-gray-400">No deposits found</p>
              {statusFilter && (
                <p className="text-sm text-gray-500 mt-1">
                  Try changing the filter or create a new deposit
                </p>
              )}
              {depositsData && depositsData.success && (
                <p className="text-sm text-blue-400 mt-1">
                  API returned success but no deposits in array
                </p>
              )}
            </div>
          ) : (
            <div className="space-y-4" key={`deposits-${page}-${statusFilter}`}>
              {deposits.map((deposit) => (
                <div key={deposit.id} className="bg-white/10 backdrop-blur-sm rounded-lg p-4 border border-white/20">
                  <div className="flex items-center justify-between">
                    <div className="flex-1">
                      <div className="flex items-center space-x-3 mb-2">
                        <div className={`px-3 py-1 rounded-full text-xs font-medium flex items-center space-x-1 ${getStatusColor(deposit.status)}`}>
                          {getStatusIcon(deposit.status)}
                          <span>{deposit.status}</span>
                        </div>
                        <span className="text-sm text-gray-400">
                          {formatDate(deposit.createdAt)}
                        </span>
                      </div>

                      <div className="grid grid-cols-1 md:grid-cols-3 gap-4 text-sm">
                        <div>
                          <span className="text-gray-400">Amount:</span>
                          <span className="text-white font-medium ml-2">
                            {formatAmount(deposit.amount)} {deposit.currency}
                          </span>
                        </div>
                        <div>
                          <span className="text-gray-400">Network:</span>
                          <span className="text-white font-medium ml-2">
                            {deposit.network || 'N/A'}
                          </span>
                        </div>
                        <div>
                          <span className="text-gray-400">Type:</span>
                          <span className="text-white font-medium ml-2">
                            {deposit.depositType || 'USDT'}
                          </span>
                        </div>
                      </div>

                      {deposit.transactionHash && (
                        <div className="mt-2">
                          <span className="text-gray-400 text-sm">Transaction Hash:</span>
                          <span className="text-white font-mono text-sm ml-2 break-all">
                            {deposit.transactionHash}
                          </span>
                        </div>
                      )}
                    </div>
                  </div>
                </div>
              ))}
            </div>
          )}

          {/* Pagination */}
          {pagination.totalPages > 1 && (
            <div className="flex items-center justify-between mt-6 pt-6 border-t border-white/20">
              <div className="text-sm text-gray-400">
                Page {pagination.currentPage} of {pagination.totalPages}
              </div>
              <div className="flex space-x-2">
                <Button
                  onClick={() => setPage(page - 1)}
                  disabled={page <= 1}
                  className="px-4 py-2 bg-white/10 text-white disabled:opacity-50 disabled:cursor-not-allowed"
                >
                  Previous
                </Button>
                <Button
                  onClick={() => setPage(page + 1)}
                  disabled={page >= pagination.totalPages}
                  className="px-4 py-2 bg-white/10 text-white disabled:opacity-50 disabled:cursor-not-allowed"
                >
                  Next
                </Button>
              </div>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default DepositHistory;
