import React, { useState } from 'react';
import { useQuery, useMutation, useQueryClient } from '@tanstack/react-query';
import { Button } from './ui/Button';
import { withdrawalAPI, adminAPI } from '../services/api';
import toast from 'react-hot-toast';

const UsdtWithdrawal = ({ onClose }) => {
  const [selectedCurrency, setSelectedCurrency] = useState('USDT');
  const [selectedNetwork, setSelectedNetwork] = useState('TRC20');
  const [withdrawalAddress, setWithdrawalAddress] = useState('');
  const [withdrawalAmount, setWithdrawalAmount] = useState('');
  const queryClient = useQueryClient();

  // Fetch admin settings
  const { data: adminSettings } = useQuery({
    queryKey: ['adminSettings'],
    queryFn: () => adminAPI.getSettings(),
  });

  const minWithdrawalAmount = adminSettings?.data?.minWithdrawalAmount || 10;

  // Network configuration
  const networks = [
    { key: 'TRC20', name: 'Tron TRC20', fee: 1, color: 'bg-blue-500' },
    { key: 'BEP20', name: 'BSC BEP20', fee: 2, color: 'bg-yellow-500' },
    { key: 'POLYGON', name: 'Polygon MATIC', fee: 3, color: 'bg-purple-600' },
    { key: 'ARBITRUM', name: 'Arbitrum ARB', fee: 4, color: 'bg-blue-600' },
    { key: 'OPTIMISM', name: 'Optimism OP', fee: 4, color: 'bg-red-500' },
    { key: 'ERC20', name: 'Ethereum ERC20', fee: 5, color: 'bg-purple-500' }
  ];

  const selectedNetworkData = networks.find(network => network.key === selectedNetwork);

  // Withdrawal mutation
  const withdrawalMutation = useMutation({
    mutationFn: (data) => withdrawalAPI.createWithdrawal(data),
    onSuccess: () => {
      toast.success('Withdrawal request submitted successfully!');
      queryClient.invalidateQueries(['walletStats']);
      queryClient.invalidateQueries(['transactions']);
      onClose();
    },
    onError: (error) => {
      toast.error(error.response?.data?.message || 'Failed to submit withdrawal request');
    },
  });

  const handleWithdrawal = () => {
    if (!withdrawalAddress.trim() || !withdrawalAmount) {
      toast.error('Please fill in all fields');
      return;
    }

    const amount = parseFloat(withdrawalAmount);
    if (amount < minWithdrawalAmount) {
      toast.error(`Minimum withdrawal amount is $${minWithdrawalAmount}`);
      return;
    }

    withdrawalMutation.mutate({
      amount: amount,
      currency: 'USDT',
      walletAddress: withdrawalAddress,
      network: selectedNetwork
    });
  };

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center p-4 z-50">
      <div className="bg-white rounded-lg w-full max-w-md max-h-[90vh] overflow-y-auto">
        {/* Header */}
        <div className="flex items-center justify-between p-4 border-b">
          <button onClick={onClose} className="p-2 hover:bg-gray-100 rounded-full">
            <svg className="w-6 h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M15 19l-7-7 7-7" />
            </svg>
          </button>
          <h2 className="text-lg font-semibold">USDT Withdrawal</h2>
          <div className="w-10"></div>
        </div>

        <div className="p-4">
          {/* Network Selection */}
          <div className="grid grid-cols-2 md:grid-cols-3 gap-2 mb-6">
            {networks.map((network) => (
              <button
                key={network.key}
                onClick={() => setSelectedNetwork(network.key)}
                className={`p-3 rounded-lg text-left transition-colors ${
                  selectedNetwork === network.key
                    ? `${network.color} text-white shadow-sm`
                    : 'bg-gray-100 text-gray-600 hover:text-gray-800'
                }`}
              >
                <div className="text-sm font-medium">{network.name}</div>
                <div className="text-xs opacity-75">${network.fee} fee</div>
              </button>
            ))}
          </div>

          {/* Withdrawal Form */}
          <div className="space-y-4">
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">
                Withdrawal Address
              </label>
              <input
                type="text"
                value={withdrawalAddress}
                onChange={(e) => setWithdrawalAddress(e.target.value)}
                placeholder="Enter your USDT wallet address"
                className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-red-500 focus:border-transparent"
              />
            </div>

            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">
                Withdrawal Amount (USDT)
              </label>
              <input
                type="number"
                value={withdrawalAmount}
                onChange={(e) => setWithdrawalAmount(e.target.value)}
                placeholder="Enter amount"
                min={minWithdrawalAmount}
                step="0.01"
                className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-red-500 focus:border-transparent"
              />
            </div>

            {/* Fee Information */}
            {selectedNetworkData && (
              <div className="bg-blue-50 border border-blue-200 rounded-lg p-3">
                <div className="text-sm text-blue-800">
                  <p className="font-medium">Network: {selectedNetworkData.name}</p>
                  <p>Fee: ${selectedNetworkData.fee} USDT</p>
                  <p>Processing time: 1-24 hours</p>
                </div>
              </div>
            )}

            {/* Action Buttons */}
            <div className="mt-6 space-y-3">
              <Button
                onClick={handleWithdrawal}
                disabled={withdrawalMutation.isLoading || !withdrawalAddress.trim() || !withdrawalAmount}
                className="w-full bg-gradient-to-r from-red-600 to-pink-600 hover:from-red-700 hover:to-pink-700 text-white"
              >
                {withdrawalMutation.isLoading ? 'Processing...' : 'Submit Withdrawal'}
              </Button>
              <Button onClick={onClose} variant="outline" className="w-full">
                Cancel
              </Button>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default UsdtWithdrawal;
